import React from "react";
import { Button, Typography } from "@mui/material";
import { useTranslation } from "react-i18next";

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange }) => {
  const { i18n } = useTranslation();

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">
        {i18n.t("dueDate")}: {task.dueDate.split("T")[0]}
      </Typography>
      <Typography variant="body2">
        {i18n.t("status")}: {i18n.t(task.status === "Pending" ? "pending" : "completed")}
      </Typography>

      <div className="task-actions">
        {/* Edit and Delete buttons */}
        <div className="edit-delete-buttons">
          <Button
            variant="contained"
            color="primary"
            onClick={() => onTaskEdit(task.id)}
            className="full-width-button"
          >
            {i18n.t("edit")}
          </Button>

          <Button
            variant="contained"
            color="secondary"
            onClick={() => onDeleteTask(task.id)}
            className="full-width-button"
          >
            {i18n.t("delete")}
          </Button>
        </div>

        {/* Change status button */}
        <Button
          variant="contained"
          color="secondary"
          onClick={() =>
            onStatusChange(task.id, task.status === "Pending" ? "Completed" : "Pending")
          }
          className="full-width-button"
        >
          {task.status === "Pending" ? i18n.t("Completed") : i18n.t("Pending")}
        </Button>
      </div>
    </div>
  );
};

export default TaskItem;
