import React, { useState, useEffect } from "react";
import { fetchTasks, fetchTaskById, updateTask, deleteTask } from "./api";
import TaskForm from "./components/TaskForm";
import { Button, Typography, Container } from "@mui/material";
import SideMenu from "./components/SideMenu";
import { useTranslation } from 'react-i18next';  // Added to import useTranslation hook
import "./index.css";
import './i18next';

const App = () => {
  const { i18n } = useTranslation();  // Destructure i18n from useTranslation hook
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [lang, setLang] = useState("en");
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const getTasks = async () => {
      const data = await fetchTasks();
      setTasks(data);
    };
    getTasks();
  }, []);

  const handleTaskCreated = () => {
    const getTasks = async () => {
      const data = await fetchTasks();
      setTasks(data);
    };
    getTasks();
  };

  const handleTaskEdit = async (taskId) => {
    try {
      const task = await fetchTaskById(taskId);
      setTaskToEdit(task); // Show the full data to edit it
    } catch (error) {
      console.error("Error fetching task for editing:", error);
    }
  };

  const handleEditComplete = () => {
    setTaskToEdit(null);
  };

  const handleStatusChange = async (taskId, status) => {
    const updatedTask = tasks.find((task) => task.id === taskId);
    if (updatedTask) {
      updatedTask.status = status;
      await updateTask(updatedTask);
      const data = await fetchTasks();
      setTasks(data);
    }
  };

  const handleDeleteTask = async (id) => {
    await deleteTask(id);
    const data = await fetchTasks();
    setTasks(data);
  };

  const toggleLanguage = () => {
    const newLang = lang === "en" ? "ar" : "en";
    setLang(newLang);
    i18n.changeLanguage(newLang); // Change language using i18n
    document.body.dir = newLang === "ar" ? "rtl" : "ltr"; // Update text direction
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <Container maxWidth="sm" className="app-container">
      <SideMenu menuOpen={menuOpen} toggleMenu={toggleMenu} toggleLanguage={toggleLanguage} lang={lang} />

      <Typography variant="h4" gutterBottom>{i18n.t('taskManagement')}</Typography> {/* Translating title */}

      <Button onClick={toggleLanguage} variant="contained" color="primary">
        {lang === "en" ? "Switch to Arabic" : "التبديل إلى الإنجليزية"} {/* Toggle language button */}
      </Button>

      <TaskForm 
        onTaskCreated={handleTaskCreated} 
        taskToEdit={taskToEdit} 
        onEditComplete={handleEditComplete} 
      />

      <div className="task-list">
        {tasks.map((task) => (
          <div key={task.id} className="task-item">
            <Typography variant="h6">{task.title}</Typography>
            <Typography variant="body1">{task.description}</Typography>
            <Typography variant="body2">Due Date: {task.dueDate}</Typography>
            <Typography variant="body2">Status: {task.status}</Typography>
            <div className="task-actions">
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleTaskEdit(task.id)}
                style={{ backgroundColor: '#4A628A', marginRight: '10px' }}
              >
                Edit
              </Button>
              <Button
                variant="contained"
                color="secondary"
                onClick={() => handleDeleteTask(task.id)}
                style={{ backgroundColor: '#4A628A' }}
              >
                Delete
              </Button>
            </div>
          </div>
        ))}
      </div>
    </Container>
  );
};

export default App;
