import React from "react";
import { Button, Typography } from "@mui/material";
import { useTranslation } from "react-i18next";
import { jsPDF } from "jspdf"; // Import jsPDF library

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange }) => {
  const { i18n } = useTranslation();

  // Function to save the task as PDF
  const saveTaskAsPDF = () => {
    const doc = new jsPDF(); // Create a new PDF document
    
    // Add task content to the PDF
    doc.text(`Task: ${task.title}`, 10, 10);
    doc.text(`Description: ${task.description}`, 10, 20);
    doc.text(`Due Date: ${task.dueDate.split("T")[0]}`, 10, 30);
    doc.text(`Status: ${i18n.t(task.status === "Pending" ? "pending" : "completed")}`, 10, 40);

    // Save the file as a PDF
    doc.save(`${task.title}_task.pdf`);
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">
        {i18n.t("dueDate")}: {task.dueDate.split("T")[0]}
      </Typography>
      <Typography variant="body2">
        {i18n.t("status")}: {i18n.t(task.status === "Pending" ? "pending" : "completed")}
      </Typography>

      <div className="task-actions">
        {/* Edit and Delete buttons */}
        <div className="edit-delete-buttons">
          <Button
            variant="contained"
            color="primary"
            onClick={() => onTaskEdit(task.id)}
            className="full-width-button"
          >
            {i18n.t("edit")}
          </Button>

          <Button
            variant="contained"
            color="secondary"
            onClick={() => onDeleteTask(task.id)}
            className="full-width-button"
          >
            {i18n.t("delete")}
          </Button>
        </div>

        {/* Change status button */}
        <Button
          variant="contained"
          color="secondary"
          onClick={() =>
            onStatusChange(task.id, task.status === "Pending" ? "Completed" : "Pending")
          }
          className="full-width-button"
        >
          {task.status === "Pending" ? i18n.t("Completed") : i18n.t("Pending")}
        </Button>

        {/* Save as PDF button */}
        <Button
          variant="contained"
          color="default"
          onClick={saveTaskAsPDF}
          className="full-width-button"
        >
          {i18n.t("saveAsPDF")}
        </Button>
      </div>
    </div>
  );
};

export default TaskItem;
