import React, { useState } from "react";
import { createTask } from "../api";
import { TextField, Button, MenuItem, Select, InputLabel, FormControl } from "@mui/material";

const TaskForm = ({ onTaskCreated }) => {
  const [taskDetails, setTaskDetails] = useState({
    title: "",
    description: "",
    dueDate: "",
    status: "Pending",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTaskDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { title, description, dueDate, status } = taskDetails;
    const newTask = { title, description, dueDate, status };
    const createdTask = await createTask(newTask);

    if (createdTask) {
      onTaskCreated();
      setTaskDetails({
        title: "",
        description: "",
        dueDate: "",
        status: "Pending",
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '500px', margin: 'auto', backgroundColor: '#FBFBFB', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
      <div style={{ marginBottom: '15px' }}>
        <TextField
          fullWidth
          label="Task Title"
          name="title"
          value={taskDetails.title}
          onChange={handleChange}
          required
          InputProps={{
            style: { backgroundColor: 'transparent', borderRadius: '5px' }
          }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <TextField
          fullWidth
          label="Task Description"
          name="description"
          value={taskDetails.description}
          onChange={handleChange}
          required
          InputProps={{
            style: { backgroundColor: 'transparent', borderRadius: '5px' }
          }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <TextField
          fullWidth
          label="Due Date"
          name="dueDate"
          type="date"
          value={taskDetails.dueDate}
          onChange={handleChange}
          required
          InputLabelProps={{
            shrink: true,
          }}
          InputProps={{
            style: { backgroundColor: 'transparent', borderRadius: '5px' }
          }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <FormControl fullWidth>
          <InputLabel>Status</InputLabel>
          <Select
            name="status"
            value={taskDetails.status}
            onChange={handleChange}
            label="Status"
            style={{ backgroundColor: 'transparent', borderRadius: '5px' }}
          >
            <MenuItem value="Pending" style={{ backgroundColor: '#B9E5E8' }}>Pending</MenuItem>
            <MenuItem value="Completed" style={{ backgroundColor: '#B9E5E8' }}>Completed</MenuItem>
          </Select>
        </FormControl>
      </div>
      <Button 
        type="submit" 
        variant="contained" 
        color="primary" 
        fullWidth
        style={{ backgroundColor: '#4A628A', color: 'white', borderRadius: '5px', padding: '10px 0' }}
        onMouseOver={(e) => e.target.style.backgroundColor = '#3A4F73'}
        onMouseOut={(e) => e.target.style.backgroundColor = '#4A628A'}
      >
        Add Task
      </Button>
    </form>
  );
};

export default TaskForm;
