import React from "react";
import { Button, Typography } from "@mui/material";
import { jsPDF } from "jspdf";  // تأكد من الاستيراد الصحيح للمكتبة
import { useTranslation } from "react-i18next";
import "./styleForm.css";

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange }) => {
  const { t } = useTranslation();

  // Function to generate PDF
  const generatePDF = () => {
    // تحقق من أن بيانات المهمة مكتملة
    if (!task || !task.title || !task.description || !task.dueDate) {
      console.error("Task data is incomplete:", task);  // سجل البيانات هنا لمراجعتها
      alert("Task data is incomplete");
      return; // إذا كانت البيانات غير مكتملة، لا نستمر في إنشاء PDF
    }

    const doc = new jsPDF();

    // سجل للتأكد من أننا نتعامل مع البيانات بشكل صحيح
    console.log("Generating PDF for task:", task);

    // إضافة تفاصيل المهمة إلى PDF
    doc.text(`Task Title: ${task.title}`, 10, 10);
    doc.text(`Description: ${task.description}`, 10, 20);
    doc.text(`Due Date: ${task.dueDate}`, 10, 30);
    doc.text(`Status: ${task.status}`, 10, 40);

    // حفظ الـ PDF
    doc.save(`${task.title}.pdf`);
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">{t("dueDate")}: {task.dueDate}</Typography>
      <Typography variant="body2">{t("status")}: {task.status}</Typography>

      {/* Buttons for editing, deleting, and status change */}
      <Button onClick={() => onTaskEdit(task.id)} variant="contained" color="primary">
        {t("edit")}
      </Button>
      <Button onClick={() => onDeleteTask(task.id)} variant="contained" color="secondary">
        {t("delete")}
      </Button>
      <Button
        onClick={() => onStatusChange(task.id, task.status === "Pending" ? "Completed" : "Pending")}
        variant="contained"
        color={task.status === "Pending" ? "primary" : "success"}
      >
        {task.status === "Pending" ? t("markCompleted") : t("markPending")}
      </Button>

      {/* Button to save task as PDF */}
      <Button onClick={generatePDF} variant="contained" color="default">
        {t("saveAsPDF")}
      </Button>
    </div>
  );
};

export default TaskItem;
