import React from "react";
import { Button, Typography } from "@mui/material";
import { useTranslation } from "react-i18next";
import jsPDF from "jspdf"; // Importing jsPDF library to generate PDF files

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange }) => {
  const { i18n } = useTranslation();

  // Function to save the task as a PDF file
  const handleSaveAsPDF = () => {
    const doc = new jsPDF(); // Create a new instance of jsPDF

    // Set the font and size for the PDF document
    doc.setFont("helvetica", "normal");
    doc.setFontSize(16);
    doc.text(i18n.t("taskDetails"), 10, 10); // Title of the PDF

    // Add task details to the PDF
    doc.setFontSize(12);
    doc.text(`${i18n.t("task")}: ${task.title}`, 10, 20); // Task title
    doc.text(`${i18n.t("description")}: ${task.description}`, 10, 30); // Task description
    doc.text(`${i18n.t("dueDate")}: ${task.dueDate.split("T")[0]}`, 10, 40); // Due date of the task
    doc.text(`${i18n.t("status")}: ${i18n.t(task.status === "Pending" ? "pending" : "completed")}`, 10, 50); // Status of the task

    // Save the PDF with the task title as the file name
    doc.save(`${task.title}_task.pdf`);
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">
        {i18n.t("dueDate")}: {task.dueDate.split("T")[0]}
      </Typography>
      <Typography variant="body2">
        {i18n.t("status")}: {i18n.t(task.status === "Pending" ? "pending" : "completed")}
      </Typography>

      <div className="task-actions">
        {/* Edit and Delete buttons */}
        <div className="edit-delete-buttons">
          <Button
            variant="contained"
            color="primary"
            onClick={() => onTaskEdit(task.id)}
            className="full-width-button"
          >
            {i18n.t("edit")}
          </Button>

          <Button
            variant="contained"
            color="secondary"
            onClick={() => onDeleteTask(task.id)}
            className="full-width-button"
          >
            {i18n.t("delete")}
          </Button>
        </div>

        {/* Change status button */}
        <Button
          variant="contained"
          color="secondary"
          onClick={() =>
            onStatusChange(task.id, task.status === "Pending" ? "Completed" : "Pending")
          }
          className="full-width-button"
        >
          {task.status === "Pending" ? i18n.t("Completed") : i18n.t("Pending")}
        </Button>

        {/* Button to save the task as a PDF */}
        <Button
          variant="contained"
          color="default"
          onClick={handleSaveAsPDF}
          className="full-width-button"
        >
          {i18n.t("saveAsPDF")}
        </Button>
      </div>
    </div>
  );
};

export default TaskItem;
