import React from 'react';
import { Drawer, List, ListItem, ListItemText, IconButton, AppBar, Toolbar, Button, Typography } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { useTranslation } from 'react-i18next';
import "./style.css";

const SideMenu = ({ menuOpen, toggleMenu, toggleLanguage, lang, userRole, onLogout }) => {
  const { t } = useTranslation();
  const drawerAnchor = lang === "ar" ? "right" : "left";

  return (
    <>
      {/* AppBar */}
      <AppBar position="fixed" className="custom-app-bar">
        <Toolbar>
          {/* Menu icon */}
          <IconButton edge="start" onClick={toggleMenu} className="menu-icon">
            <MenuIcon />
          </IconButton>
          
          {/* Centered Title */}
          <Typography 
            variant="h6" 
            sx={{ 
              position: "absolute", 
              left: "50%", 
              transform: "translateX(-50%)", 
              color: "#4A628A" 
            }}
          >
            {userRole === "admin"
              ? t("adminDashboard")
              : `${t("userDashboard")} (${userRole})`}
          </Typography>
          
          {/* Logout button */}
          <Button 
            color="inherit" 
            onClick={onLogout}
            className="logout-button"
          >
            {t("logout")}
          </Button>
        </Toolbar>
      </AppBar>

      {/* Drawer for the side menu wrapped in a container with LTR direction */}
      <div style={{ direction: "ltr" }}>
        <Drawer
          anchor={drawerAnchor}
          open={menuOpen}
          onClose={toggleMenu}
          PaperProps={{
            style: drawerAnchor === "left" 
              ? { left: 0, right: "auto" } 
              : { right: 0, left: "auto" },
          }}
        >
          <List>
            <ListItem button onClick={toggleMenu}>
              <ListItemText primary={t("home")} />
            </ListItem>
            <ListItem button onClick={toggleMenu}>
              <ListItemText primary={t("tasks")} />
            </ListItem>
            <ListItem button onClick={toggleMenu}>
              <ListItemText primary={t("settings")} />
            </ListItem>
            {/* Language toggle */}
            <ListItem button onClick={toggleLanguage}>
              <ListItemText primary={lang === "en" ? t("arabicLanguage") : t("englishLanguage")} />
            </ListItem>
          </List>
        </Drawer>
      </div>
    </>
  );
};

export default SideMenu;
