import React from "react";
import { jsPDF } from "jspdf";
import { Button } from "@mui/material";
import { useTranslation } from 'react-i18next';

const TaskItem = ({ tasks }) => {
  const { i18n } = useTranslation();

  // Function to save a single task as a PDF
  const saveTaskAsPDF = (task) => {
    const doc = new jsPDF();

    // Add task details to the PDF
    doc.setFont("helvetica", "normal");
    doc.text(i18n.t("taskDetails"), 10, 10);
    doc.text(`${i18n.t("title")}: ${task.title}`, 10, 20);
    doc.text(`${i18n.t("description")}: ${task.description}`, 10, 30);
    doc.text(`${i18n.t("status")}: ${i18n.t(task.status === 'Pending' ? 'pending' : 'completed')}`, 10, 40);

    // Save the PDF with a unique filename
    doc.save(`${task.title}.pdf`);
  };

  // Function to save all tasks as a single PDF
  const saveAllTasksAsPDF = () => {
    const doc = new jsPDF();

    tasks.forEach((task, index) => {
      if (index > 0) doc.addPage(); // Add a new page for each task

      // Add task details to the PDF
      doc.setFont("helvetica", "normal");
      doc.text(i18n.t("taskDetails"), 10, 10);
      doc.text(`${i18n.t("title")}: ${task.title}`, 10, 20);
      doc.text(`${i18n.t("description")}: ${task.description}`, 10, 30);
      doc.text(`${i18n.t("status")}: ${i18n.t(task.status === 'Pending' ? 'pending' : 'completed')}`, 10, 40);
    });

    // Save the PDF with a unique filename
    doc.save("All_Tasks.pdf");
  };

  return (
    <div>
      <h2>{i18n.t("taskList")}</h2>

      {tasks.map((task) => (
        <div key={task.id} className="task-item">
          <h3>{task.title}</h3>
          <p>{task.description}</p>

          {/* Button to save the current task as a PDF */}
          <Button
            variant="contained"
            color="primary"
            onClick={() => saveTaskAsPDF(task)}
          >
            {i18n.t("saveAsPDF")}
          </Button>
        </div>
      ))}

      {/* Button to save all tasks as a single PDF */}
      <Button
        variant="contained"
        color="secondary"
        onClick={saveAllTasksAsPDF}
      >
        {i18n.t("saveAllAsPDF")}
      </Button>
    </div>
  );
};

export default TaskItem;
