import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  CardContent,
  Grid,
  Typography,
  Box,
  Badge
} from "@mui/material";
import AssignmentIcon from "@mui/icons-material/Assignment";
import PeopleIcon from "@mui/icons-material/People";
import CircleIcon from "@mui/icons-material/Circle";
import { useTranslation } from "react-i18next";
import "./style.css";

const Dashboard = ({ userRole, selectedEntity }) => {
  const { t } = useTranslation();
  const [data, setData] = useState({ tasksCount: 0, usersCount: 0 });
  const [priorityCounts, setPriorityCounts] = useState({});
  const [selectedPriority, setSelectedPriority] = useState("");

  // Priority configuration: label, color, and the actual value used in API
  const priorityList = [
    { value: "Red", label: "High Priority", color: "red" },
    { value: "Green", label: "Medium Priority", color: "green" },
    { value: "Gray", label: "Low Priority", color: "gray" },
  ];

  useEffect(() => {
    let url = "https://localhost:7228/api/tasks/dashboard";
    if (userRole && userRole.toLowerCase() !== "admin" && selectedEntity) {
      url += `?entityId=${selectedEntity}`;
    }
    axios
      .get(url)
      .then((response) => setData(response.data))
      .catch((err) => console.error("Error fetching dashboard data:", err));
  }, [userRole, selectedEntity]);

  // Fetch priority counts for the dashboard
  useEffect(() => {
    let url = "https://localhost:7228/api/tasks/priority-count";
    if (userRole && userRole.toLowerCase() !== "admin" && selectedEntity) {
      url += `?entityId=${selectedEntity}`;
    }
    axios
      .get(url)
      .then((response) => setPriorityCounts(response.data))
      .catch((err) => console.error("Error fetching priority counts:", err));
  }, [userRole, selectedEntity]);

  // Handle click on a priority circle
  const handlePriorityClick = (priorityValue) => {
    setSelectedPriority(priorityValue);
    // Optionally, you could call an API or trigger a filter action here
  };

  return (
    <div className="dashboard-container">
      <Typography variant="h4" gutterBottom>
        {userRole === "admin"
          ? t("adminDashboard")
          : `${t("userDashboard")} (${userRole})`}
      </Typography>

      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <Card className="dashboard-card">
            <CardContent>
              <div className="dashboard-icon-container">
                <AssignmentIcon className="dashboard-icon" />
              </div>
              <Typography variant="h6" component="p">
                {t("totalTasks")}
              </Typography>
              <Typography variant="h4" component="p">
                {data.tasksCount}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Card className="dashboard-card">
            <CardContent>
              <div className="dashboard-icon-container">
                <PeopleIcon className="dashboard-icon" />
              </div>
              <Typography variant="h6" component="p">
                {t("totalUsers")}
              </Typography>
              <Typography variant="h4" component="p">
                {data.usersCount}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Priority Section */}
      <Box display="flex" justifyContent="center" marginTop={3}>
        {priorityList.map(({ value, label, color }) => (
          <Box
            key={value}
            onClick={() => handlePriorityClick(value)}
            sx={{
              display: "flex",
              alignItems: "center",
              cursor: "pointer",
              margin: "0 1rem",
            }}
          >
            {/* Colored circle */}
            <CircleIcon sx={{ color, fontSize: 20, marginRight: "0.5rem" }} />
            {/* Priority label */}
            <Typography variant="body1" sx={{ marginRight: "0.5rem" }}>
              {label}
            </Typography>
            {/* Task count for this priority */}
            <Badge badgeContent={priorityCounts[value] || 0} color="primary" />
          </Box>
        ))}
      </Box>

      {/* Display the selected priority label (optional) */}
      {selectedPriority && (
        <Typography variant="h6" align="center" marginTop={2}>
          {t("filterByPriority")} :{" "}
          {
            priorityList.find((item) => item.value === selectedPriority)?.label
          }
        </Typography>
      )}
    </div>
  );
};

export default Dashboard;
