// src/components/TaskItem.js
import React from "react";
import { Button, Typography } from "@mui/material";
import { FaFilePdf } from 'react-icons/fa';  // PDF Icon
import { jsPDF } from "jspdf";  // Make sure jsPDF is installed

const TaskItem = ({ task }) => {

  // Function to generate PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);

    // Add task details to the PDF
    doc.text("Task Title: " + task.title, 10, 10);
    doc.text("Description: " + task.description, 10, 20);
    doc.text("Due Date: " + task.dueDate.split('T')[0], 10, 30);  // Only show the date
    doc.text("Status: " + task.status, 10, 40);

    // Save the PDF
    doc.save(task.title + ".pdf");
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">Due Date: {task.dueDate.split('T')[0]}</Typography>
      <Typography variant="body2">Status: {task.status}</Typography>

      {/* Button to export the task to PDF */}
      <Button
        variant="contained"
        color="default"
        onClick={generatePDF}
        className="full-width-button"
      >
        <FaFilePdf size={20} /> Export to PDF
      </Button>
    </div>
  );
};

export default TaskItem;
