// TaskForm.js (Located outside src folder)
import React, { useState, useEffect } from "react";
import { TextField, Button, FormControl, InputLabel, Select, MenuItem } from "@mui/material";
import { useTranslation } from "react-i18next";

const TaskForm = ({ taskToEdit, onTaskCreated, onEditComplete }) => {
  const { t } = useTranslation();

  // Set initial task details state
  const [taskDetails, setTaskDetails] = useState({
    title: "",
    description: "",
    dueDate: "",
    status: "Pending", // Default status set to "Pending"
  });

  // If taskToEdit is provided, update the form with existing task details
  useEffect(() => {
    if (taskToEdit) {
      setTaskDetails({
        title: taskToEdit.title || "", // Ensure the title field is not empty
        description: taskToEdit.description || "",
        dueDate: taskToEdit.dueDate || "",
        status: taskToEdit.status || "Pending", // Default status if task doesn't have it
      });
    }
  }, [taskToEdit]);

  // Handle changes to form input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTaskDetails({
      ...taskDetails,
      [name]: value,
    });
  };

  // Handle form submission (task creation or editing)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskDetails.title && taskDetails.description) {
      onTaskCreated(taskDetails); // Create a new task with the provided details
      setTaskDetails({
        title: "",
        description: "",
        dueDate: "",
        status: "Pending", // Reset the form after submitting
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      {/* Title input */}
      <TextField
        fullWidth
        label={t("taskTitle")}
        name="title"
        value={taskDetails.title}
        onChange={handleChange}
        required
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      
      {/* Description input */}
      <TextField
        fullWidth
        label={t("taskDescription")}
        name="description"
        value={taskDetails.description}
        onChange={handleChange}
        required
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      
      {/* Due date input */}
      <TextField
        fullWidth
        label={t("dueDate")}
        name="dueDate"
        type="date"
        value={taskDetails.dueDate}
        onChange={handleChange}
        required
        InputLabelProps={{ shrink: true }}
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      
      {/* Status dropdown */}
      <FormControl fullWidth>
        <InputLabel>{t("status")}</InputLabel>
        <Select
          name="status"
          value={taskDetails.status}
          onChange={handleChange}
          label={t("status")}
          style={{ backgroundColor: "transparent", borderRadius: "5px" }}
        >
          <MenuItem value="Pending">{t("pending")}</MenuItem>
          <MenuItem value="Completed">{t("completed")}</MenuItem>
        </Select>
      </FormControl>
      
      {/* Submit button */}
      <Button type="submit" variant="contained" color="primary" fullWidth>
        {taskToEdit ? t("editTask") : t("addTask")}
      </Button>

      {/* Cancel button for editing a task */}
      {taskToEdit && (
        <Button
          onClick={onEditComplete}
          variant="outlined"
          color="secondary"
          fullWidth
        >
          {t("cancelEdit")}
        </Button>
      )}
    </form>
  );
};

export default TaskForm;
