import React, { useState, useEffect } from "react";
import { fetchTasks, fetchTaskById, createTask, updateTask, deleteTask } from "./api";
import TaskForm from "./components/TaskForm";
import TaskItem from "./components/TaskItem"; 
import { Typography, Container } from "@mui/material";
import SideMenu from "./components/SideMenu";
import { useTranslation } from "react-i18next";
import { jsPDF } from "jspdf";
import "./index.css";
import "./i18next";
import './components/style.css';

const App = () => {
  const { i18n } = useTranslation();
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [lang, setLang] = useState("en");
  const [menuOpen, setMenuOpen] = useState(false);
  const [selectedEntity, setSelectedEntity] = useState(null);

  // Fetch tasks when the component mounts or entity changes
  useEffect(() => {
    const getTasks = async () => {
      try {
        const data = await fetchTasks(selectedEntity);
        setTasks(data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };
    getTasks();
  }, [selectedEntity]);

  // Handle task creation
  const handleTaskCreated = async (task) => {
    try {
      if (!task.title || !task.description || !task.dueDate || !task.entityId) {
        alert("All fields are required.");
        return;
      }

      await createTask(task);
      const updatedTasks = await fetchTasks(selectedEntity);
      setTasks(updatedTasks);
    } catch (error) {
      console.error("Failed to create task:", error);
      alert(error.message);
    }
  };

  // Handle task update
  const handleTaskUpdate = async (updatedTask) => {
    try {
      if (!updatedTask.id) {
        alert("Task ID is required for update.");
        return;
      }

      await updateTask(updatedTask);
      const data = await fetchTasks(selectedEntity);
      setTasks(data);
    } catch (error) {
      console.error("Failed to update task:", error);
      alert(error.message);
    }
  };

  // Fetch task for editing
  const handleTaskEdit = async (taskId) => {
    try {
      const task = await fetchTaskById(taskId);
      setTaskToEdit(task);
    } catch (error) {
      console.error("Error fetching task for editing:", error);
    }
  };

  // Clears editing mode after updating
  const handleEditComplete = () => {
    setTaskToEdit(null);
  };

  // Toggle task status
  const handleStatusChange = async (taskId, status) => {
    try {
      const task = tasks.find((t) => t.id === taskId);
      if (!task) {
        alert("Task not found.");
        return;
      }

      const updatedTask = { ...task, status };
      await updateTask(updatedTask);
      const data = await fetchTasks(selectedEntity);
      setTasks(data);
    } catch (error) {
      console.error("Error updating task status:", error);
      alert(error.message);
    }
  };

  // Delete a task
  const handleDeleteTask = async (id) => {
    try {
      if (!id) {
        alert("Task ID is required for deletion.");
        return;
      }

      await deleteTask(id);
      const data = await fetchTasks(selectedEntity);
      setTasks(data);
    } catch (error) {
      console.error("Error deleting task:", error);
      alert(error.message);
    }
  };

  // Function to generate and share task PDF via WhatsApp
  const shareTask = async (task) => {
    try {
      const doc = new jsPDF();
      
      doc.text(`Title: ${task.title}`, 10, 20);
      doc.text(`Description: ${task.description}`, 10, 30);
      doc.text(`Due Date: ${task.dueDate ? task.dueDate.split("T")[0] : "N/A"}`, 10, 40);
      doc.text(`Status: ${task.status}`, 10, 50);

      const pdfDataUri = doc.output("dataurlstring");

      const message = `*Task Details:*\n`
        + `*Title:* ${task.title}\n`
        + `*Due Date:* ${task.dueDate ? task.dueDate.split("T")[0] : "N/A"}\n`
        + `*Status:* ${task.status}\n\n`
        + `*Download PDF:* ${pdfDataUri}`;

      const whatsappUrl = `https://wa.me/966582144870?text=${encodeURIComponent(message)}`;
      window.open(whatsappUrl, "_blank");
    } catch (error) {
      console.error("Error sharing task:", error);
      alert("Failed to share task via WhatsApp.");
    }
  };

  return (
    <Container maxWidth="sm" className="app-container">
      <SideMenu
        menuOpen={menuOpen}
        toggleMenu={() => setMenuOpen(!menuOpen)}
        toggleLanguage={() => {
          const newLang = lang === "en" ? "ar" : "en";
          setLang(newLang);
          i18n.changeLanguage(newLang);
          document.body.dir = newLang === "ar" ? "rtl" : "ltr";
        }}
        lang={lang}
      />
      <Typography variant="h4" gutterBottom>
        {i18n.t("taskManagement")}
      </Typography>

      {/* Entity Selection Dropdown */}
      <select onChange={(e) => setSelectedEntity(e.target.value)} className="form-field">
        <option value="">All Tasks</option>
        <option value="1">User1</option>
        <option value="2">User2</option>
      </select>

      <TaskForm
        onTaskCreated={handleTaskCreated} 
        taskToEdit={taskToEdit}
        onEditComplete={handleEditComplete}
        onUpdateTask={handleTaskUpdate}
      />
      <div className="task-list">
        {tasks.length > 0 && tasks.map((task) => (
          <TaskItem
            key={task.id}
            task={task}
            onEdit={handleTaskEdit}
            onDelete={handleDeleteTask}
            onStatusChange={handleStatusChange}
            onShareTask={shareTask}
          />
        ))}
      </div>
    </Container>
  );
};

export default App;
