import React, { useState, useEffect } from "react";
import { Button, TextField } from "@mui/material";
import { useTranslation } from 'react-i18next';
import "../styleForm.css";

const TaskForm = ({ onTaskCreated, taskToEdit, onEditComplete }) => {
  const { i18n } = useTranslation();
  const [task, setTask] = useState({ title: "", description: "", dueDate: "", status: "" });

  useEffect(() => {
    if (taskToEdit) {
      setTask(taskToEdit);
    }
  }, [taskToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTask({ ...task, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskToEdit) {
      onEditComplete();
    } else {
      onTaskCreated();
    }
    setTask({ title: "", description: "", dueDate: "", status: "" });
  };

  return (
    <form className="task-form" onSubmit={handleSubmit}>
      <TextField
        name="title"
        label={i18n.t('title')}
        value={task.title}
        onChange={handleChange}
        fullWidth
        margin="normal"
      />
      <TextField
        name="description"
        label={i18n.t('description')}
        value={task.description}
        onChange={handleChange}
        fullWidth
        margin="normal"
      />
      <TextField
        name="dueDate"
        type="date"
        label={i18n.t('dueDate')}
        value={task.dueDate}
        onChange={handleChange}
        fullWidth
        margin="normal"
        InputLabelProps={{ shrink: true }}
      />
      <TextField
        name="status"
        label={i18n.t('status')}
        value={task.status}
        onChange={handleChange}
        fullWidth
        margin="normal"
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        style={{ width: '100%', marginTop: '10px' }}
      >
        {taskToEdit ? i18n.t('save') : i18n.t('addTask')}
      </Button>
    </form>
  );
};

export default TaskForm;
