import React from "react";
import { Button, Typography } from "@mui/material";
import { FaFilePdf } from 'react-icons/fa';  // PDF Icon
import { jsPDF } from "jspdf";  // Make sure jsPDF is installed

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange, i18n }) => {

  // Function to generate PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);

    // Add task details to the PDF
    doc.text("Task Title: " + task.title, 10, 10);
    doc.text("Description: " + task.description, 10, 20);
    doc.text("Due Date: " + task.dueDate.split('T')[0], 10, 30);  // Only show the date
    doc.text("Status: " + task.status, 10, 40);

    // Save the PDF
    doc.save(task.title + ".pdf");
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>
      <Typography variant="body1">{task.description}</Typography>
      <Typography variant="body2">{i18n.t('dueDate')}: {task.dueDate.split('T')[0]}</Typography>
      <Typography variant="body2">{i18n.t('status')}: {i18n.t(task.status === 'Pending' ? 'pending' : 'completed')}</Typography>

      <div className="task-actions">
        {/* Edit and Delete buttons on the same row */}
        <div className="edit-delete-buttons">
          <Button
            variant="contained"
            color="primary"
            onClick={() => onTaskEdit(task.id)}
            className="full-width-button"
          >
            {i18n.t('edit')}
          </Button>

          <Button
            variant="contained"
            color="secondary"
            onClick={() => onDeleteTask(task.id)}
            className="full-width-button"
          >
            {i18n.t('delete')}
          </Button>
        </div>

        {/* Change status button */}
        <Button
          variant="contained"
          color="secondary"
          onClick={() => onStatusChange(task.id, task.status === 'Pending' ? 'Completed' : 'Pending')}
          className="full-width-button"
        >
          {task.status === 'Pending' ? i18n.t('Completed') : i18n.t('Pending')}
        </Button>

        {/* Export to PDF button */}
        <Button
          variant="contained"
          color="default"
          onClick={generatePDF} // Generate PDF when clicked
          className="full-width-button"
        >
          <FaFilePdf size={20} /> {i18n.t('exportToPDF')}
        </Button>
      </div>
    </div>
  );
};

export default TaskItem;
