import React, { useState, useEffect } from "react";
import { createTask, updateTask } from "../api"; 
import { TextField, Button, MenuItem, Select, InputLabel, FormControl } from "@mui/material";
import './styleForm.css'; // Import the external CSS file

const TaskForm = ({ onTaskCreated, taskToEdit, onEditComplete }) => {
  // Initialize task details state, pre-filling with taskToEdit if available
  const [taskDetails, setTaskDetails] = useState({
    title: taskToEdit ? taskToEdit.title : "",
    description: taskToEdit ? taskToEdit.description : "",
    dueDate: taskToEdit ? taskToEdit.dueDate : "",
    status: taskToEdit ? taskToEdit.status : "Pending",
  });

  // Update the form when taskToEdit changes
  useEffect(() => {
    setTaskDetails({
      title: taskToEdit ? taskToEdit.title : "",
      description: taskToEdit ? taskToEdit.description : "",
      dueDate: taskToEdit ? taskToEdit.dueDate : "",
      status: taskToEdit ? taskToEdit.status : "Pending",
    });
  }, [taskToEdit]);

  // Handle changes in the input fields and update the state
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTaskDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value, // Update the relevant field dynamically
    }));
  };

  // Handle form submission for adding or editing a task
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form behavior
    const { title, description, dueDate, status } = taskDetails;

    // Ensure all required fields are filled out
    if (!title || !description || !dueDate) {
      alert("Please fill all required fields!");
      return;
    }

    try {
      if (taskToEdit) {
        // If editing an existing task, update the task
        const updatedTask = { id: taskToEdit.id, title, description, dueDate, status };
        const response = await updateTask(updatedTask);

        if (response) {
          alert("Task updated successfully!");
          onEditComplete(); // Notify parent to close the form
        } else {
          alert("Failed to update task!");
        }
      } else {
        // If creating a new task
        const newTask = { title, description, dueDate, status };
        const createdTask = await createTask(newTask);

        if (createdTask) {
          alert("Task created successfully!");
          onTaskCreated(); // Notify parent to refresh task list
          setTaskDetails({
            title: "",
            description: "",
            dueDate: "",
            status: "Pending",
          });
        }
      }
    } catch (error) {
      console.error("Error saving task:", error);
      alert(`Error: ${error.message || "Failed to save the task."}`);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      {/* Task Title Input */}
      <div className="input-field">
        <TextField
          fullWidth
          label="Task Title"
          name="title"
          value={taskDetails.title}
          onChange={handleChange}
          required
          InputProps={{
            style: { backgroundColor: "transparent", borderRadius: "5px" },
          }}
        />
      </div>

      {/* Task Description Input */}
      <div className="input-field">
        <TextField
          fullWidth
          label="Task Description"
          name="description"
          value={taskDetails.description}
          onChange={handleChange}
          required
          InputProps={{
            style: { backgroundColor: "transparent", borderRadius: "5px" },
          }}
        />
      </div>

      {/* Task Due Date Input */}
      <div className="input-field">
        <TextField
          fullWidth
          label="Due Date"
          name="dueDate"
          type="date"
          value={taskDetails.dueDate}
          onChange={handleChange}
          required
          InputLabelProps={{ shrink: true }}
          InputProps={{
            style: { backgroundColor: "transparent", borderRadius: "5px" },
          }}
        />
      </div>

      {/* Task Status Dropdown */}
      <div className="input-field">
        <FormControl fullWidth>
          <InputLabel>Status</InputLabel>
          <Select
            name="status"
            value={taskDetails.status}
            onChange={handleChange}
            label="Status"
            style={{ backgroundColor: "transparent", borderRadius: "5px" }}
          >
            <MenuItem value="Pending">Pending</MenuItem>
            <MenuItem value="Completed">Completed</MenuItem>
          </Select>
        </FormControl>
      </div>

      {/* Submit Button */}
      <Button
        type="submit"
        variant="contained"
        color="primary"
        fullWidth
        className="button-submit"
      >
        {taskToEdit ? "Edit Task" : "Add Task"}
      </Button>

      {/* Cancel Button for Editing */}
      {taskToEdit && (
        <Button
          onClick={() => onEditComplete()}
          variant="outlined"
          color="secondary"
          fullWidth
          className="button-cancel"
        >
          Cancel Edit
        </Button>
      )}
    </form>
  );
};

export default TaskForm;
