import React, { useState, useEffect } from "react";
import { fetchTasks, deleteTask, updateTask } from "./api";
import TaskForm from "./components/TaskForm";
import Button from "@mui/material/Button";

const App = () => {
  const [tasks, setTasks] = useState([]);

  // Function to load tasks from the API
  const loadTasks = async () => {
    try {
      const data = await fetchTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to load tasks:", error);
    }
  };

  // Function to handle task creation
  const handleTaskCreated = () => {
    loadTasks();
  };

  // Function to handle task deletion
  const handleTaskDelete = async (id) => {
    try {
      await deleteTask(id);
      loadTasks();
    } catch (error) {
      console.error("Failed to delete task:", error);
    }
  };

  // Function to handle task update (status change)
  const handleTaskUpdate = async (id, updatedStatus) => {
    try {
      await updateTask(id, { status: updatedStatus });
      loadTasks(); // Reload tasks after update
    } catch (error) {
      console.error("Failed to update task:", error);
    }
  };

  // Load tasks when the component is mounted
  useEffect(() => {
    loadTasks();
  }, []);

  return (
    <div>
      <h1>Task Management</h1>
      <TaskForm onTaskCreated={handleTaskCreated} />
      <ul>
        {tasks.map((task) => (
          <li key={task.id} className="task">
            <div>
              <div className="task-title">{task.title}</div>
              <div className="task-description">{task.description}</div>
              <div className="task-date">{task.dueDate}</div>
              <div className={`task-status ${task.status.toLowerCase()}`}>
                {task.status}
              </div>
            </div>
            <div>
              <Button
                variant="contained"
                className="edit-button"
                onClick={() =>
                  handleTaskUpdate(
                    task.id,
                    task.status === "Pending" ? "Completed" : "Pending"
                  )
                }
              >
                Edit Status
              </Button>
              <Button
                variant="contained"
                className="delete-button"
                onClick={() => handleTaskDelete(task.id)}
              >
                Delete
              </Button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
