// TaskForm.js (Located outside src folder)
import React, { useState, useEffect } from "react";
import { TextField, Button, FormControl, InputLabel, Select, MenuItem } from "@mui/material";
import { useTranslation } from "react-i18next";

const TaskForm = ({ taskToEdit, onTaskCreated, onEditComplete }) => {
  const { t } = useTranslation();

  // Set initial task details state
  const [taskDetails, setTaskDetails] = useState({
    title: "",
    description: "",
    dueDate: "",
    state: "Pending", // Default state set to "Pending"
  });

  // If taskToEdit is provided, update the form with existing task details
  useEffect(() => {
    if (taskToEdit) {
      setTaskDetails({
        title: taskToEdit.title || "",
        description: taskToEdit.description || "",
        dueDate: taskToEdit.dueDate || "",
        state: taskToEdit.state || "Pending",
      });
    }
  }, [taskToEdit]);

  // Handle changes to form input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTaskDetails({
      ...taskDetails,
      [name]: value,
    });
  };

  // Handle form submission (task creation or editing)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskDetails.title && taskDetails.description) {
      const formattedDate = taskDetails.dueDate.replace(/-/g, "/"); // Format DD/MM/YYYY
      onTaskCreated({ ...taskDetails, dueDate: formattedDate }); // Create a new task with the provided details
      setTaskDetails({
        title: "",
        description: "",
        dueDate: "",
        state: "Pending", // Reset the form after submitting
      });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      <h2 className="form-title">{t("taskManagement")}</h2>
      
      {/* Title input */}
      <TextField
        fullWidth
        label={t("title")}
        name="title"
        value={taskDetails.title}
        onChange={handleChange}
        required
        className="input-field"
      />
      
      {/* Description input */}
      <TextField
        fullWidth
        label={t("description")}
        name="description"
        value={taskDetails.description}
        onChange={handleChange}
        required
        className="input-field"
      />
      
      {/* Due date input */}
      <TextField
        fullWidth
        label={t("dueDate")}
        name="dueDate"
        type="date"
        value={taskDetails.dueDate}
        onChange={handleChange}
        required
        InputLabelProps={{ shrink: true }}
        className="input-field"
      />
      
      {/* State dropdown */}
      <FormControl fullWidth>
        <InputLabel>{t("state")}</InputLabel>
        <Select
          name="state"
          value={taskDetails.state}
          onChange={handleChange}
          label={t("state")}
          className="input-field"
        >
          <MenuItem value="Pending">{t("pending")}</MenuItem>
          <MenuItem value="Completed">{t("completed")}</MenuItem>
        </Select>
      </FormControl>
      
      {/* Submit button */}
      <Button type="submit" variant="contained" className="button-submit">
        {taskToEdit ? t("save") : t("addTask")}
      </Button>

      {/* Edit buttons */}
      {taskToEdit && (
        <div className="edit-buttons">
          <Button onClick={onEditComplete} variant="outlined" className="button-cancel">
            {t("cancelEdit")}
          </Button>
          <Button onClick={handleSubmit} variant="contained" className="button-submit">
            {t("save")}
          </Button>
        </div>
      )}
    </form>
  );
};

export default TaskForm;
