    <div>
      {!isAuthenticated ? (
        <button onClick={() => loginWithRedirect()}>Login</button> // Login button
      ) : (
        <>
          <h2>Welcome {user.name} ðŸ‘‹</h2>
          <button onClick={() => logout({ returnTo: window.location.origin })}>
            Logout
          </button> {/* Logout button */}
        </>
      )}
    </div>
  );
};

export default Home;
