import React, { useState } from 'react';
import { Drawer, List, ListItem, ListItemText, IconButton, AppBar, Toolbar } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import "./style.css";

const SideMenu = ({ menuOpen, toggleMenu, toggleLanguage, lang }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state

  const handleLoginLogout = () => {
    setIsLoggedIn(!isLoggedIn); // Toggle login state
  };

  return (
    <>
      {/* AppBar to fix the menu and icons at the top of the page */}
      <AppBar position="static" 
      style={{ 
         backgroundColor: 'transparent',
         boxShadow: 'none'
          }}>
            
        <Toolbar>
          {/* Side menu button at the top left of the page */}
          <IconButton
            edge="start"
            onClick={toggleMenu}
            style={{ color: '#f9f9f9' }}
          >
            <MenuIcon />
          </IconButton>

          {/* Login/Logout button at the top right of the page */}
          <div style={{ flexGrow: 1 }} /> {/* This element pushes the icon to the right */}
          <IconButton
            edge="end"
            onClick={handleLoginLogout}
            style={{ color: '#f9f9f9' }}
          >
            <AccountCircle />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Drawer for the side menu */}
      <Drawer open={menuOpen} onClose={toggleMenu}>
        <List>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Home" : "الرئيسية"} />
          </ListItem>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Tasks" : "المهام"} />
          </ListItem>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Settings" : "الإعدادات"} />
          </ListItem>
          {/* Language toggle */}
          <ListItem button onClick={toggleLanguage}>
            <ListItemText primary={lang === "en" ? "Arabic Language" : "اللغة الإنجليزية"} />
          </ListItem>
        </List>
      </Drawer>
    </>
  );
};

export default SideMenu;
