import React from 'react';
import { Drawer, List, ListItem, ListItemText, IconButton, AppBar, Toolbar } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import { useAuth } from '../auth/useAuth'; // Import useAuth for authentication
import "./style.css";

const SideMenu = ({ menuOpen, toggleMenu, toggleLanguage, lang }) => {
  const { login, logout, isAuthenticated } = useAuth(); // Use Auth0 for authentication

  return (
    <>
      {/* Transparent AppBar with full width */}
      <AppBar
        position="fixed"
        className="custom-app-bar" // Custom class for AppBar styling
      >
        <Toolbar>
          {/* Menu icon for opening the side menu */}
          <IconButton
            edge="start"
            onClick={toggleMenu}
            className="menu-icon" // Custom class for menu icon styling
          >
            <MenuIcon />
          </IconButton>

          {/* Pushes the account icon to the right */}
          <div style={{ flexGrow: 1 }} />

          {/* Account icon for login/logout */}
          <IconButton
            edge="end"
            onClick={isAuthenticated ? logout : login} // Toggle between login and logout using Auth0
            className="account-icon" // Custom class for account icon styling
          >
            <AccountCircle />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Drawer for the side menu */}
      <Drawer open={menuOpen} onClose={toggleMenu}>
        <List>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Home" : "الرئيسية"} />
          </ListItem>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Tasks" : "المهام"} />
          </ListItem>
          <ListItem button onClick={toggleMenu}>
            <ListItemText primary={lang === "en" ? "Settings" : "الإعدادات"} />
          </ListItem>
          {/* Language toggle */}
          <ListItem button onClick={toggleLanguage}>
            <ListItemText primary={lang === "en" ? "Arabic Language" : "اللغة الإنجليزية"} />
          </ListItem>
        </List>
      </Drawer>
    </>
  );
};

export default SideMenu;
