const TaskForm = ({ onTaskCreated, taskToEdit, onEditComplete }) => {
  const { t } = useTranslation();

  const [taskDetails, setTaskDetails] = useState({
    title: taskToEdit ? taskToEdit.title : "",
    description: taskToEdit ? taskToEdit.description : "",
    dueDate: taskToEdit ? taskToEdit.dueDate : "",
    status: taskToEdit ? taskToEdit.status : t("pending"), // Make sure the default status is a valid string
  });

  useEffect(() => {
    if (taskToEdit) {
      setTaskDetails({
        title: taskToEdit.title || "",
        description: taskToEdit.description || "",
        dueDate: taskToEdit.dueDate || "",
        status: taskToEdit.status || t("pending"),
      });
    }
  }, [taskToEdit, t]);

  // Form submission logic and other handlers...

  return (
    <form onSubmit={handleSubmit} className="task-form">
      {/* Task Title Input */}
      <TextField
        fullWidth
        label={t("taskTitle")}
        name="title"
        value={taskDetails.title}
        onChange={handleChange}
        required
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      {/* Task Description Input */}
      <TextField
        fullWidth
        label={t("taskDescription")}
        name="description"
        value={taskDetails.description}
        onChange={handleChange}
        required
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      {/* Task Due Date Input */}
      <TextField
        fullWidth
        label={t("dueDate")}
        name="dueDate"
        type="date"
        value={taskDetails.dueDate}
        onChange={handleChange}
        required
        InputLabelProps={{ shrink: true }}
        InputProps={{
          style: { backgroundColor: "transparent", borderRadius: "5px" },
        }}
      />
      {/* Task Status Dropdown */}
      <FormControl fullWidth>
        <InputLabel>{t("status")}</InputLabel>
        <Select
          name="status"
          value={taskDetails.status || ""}
          onChange={handleChange}
          label={t("status")}
          style={{ backgroundColor: "transparent", borderRadius: "5px" }}
        >
          <MenuItem value="Pending">{t("pending")}</MenuItem>
          <MenuItem value="Completed">{t("completed")}</MenuItem>
        </Select>
      </FormControl>
      {/* Submit Button */}
      <Button type="submit" variant="contained" color="primary" fullWidth>
        {taskToEdit ? t("editTask") : t("addTask")}
      </Button>
      {/* Cancel Button for Editing */}
      {taskToEdit && (
        <Button onClick={() => onEditComplete()} variant="outlined" color="secondary" fullWidth>
          {t("cancelEdit")}
        </Button>
      )}
    </form>
  );
};
