import React from "react";
import { Button, Typography } from "@mui/material";
import { jsPDF } from "jspdf";  // Ensure the correct import of jsPDF
import { useTranslation } from "react-i18next";
import "./styleForm.css";

const TaskItem = ({ task, onTaskEdit, onDeleteTask, onStatusChange, onGeneratePDF }) => {
  const { t } = useTranslation();

  // Function to generate PDF for a single task
  const generatePDF = () => {
    // Check if task data is complete before generating PDF
    if (!task || !task.title || !task.description || !task.dueDate) {
      alert("Task data is incomplete");  // Alert the user if data is missing
      return;
    }

    const doc = new jsPDF();  // Create a new jsPDF document
    // Add text content for task details in the PDF
    doc.text(`Task Title: ${task.title}`, 10, 10);
    doc.text(`Description: ${task.description}`, 10, 20);
    doc.text(`Due Date: ${task.dueDate}`, 10, 30);
    doc.text(`Status: ${task.status}`, 10, 40);

    // Save the generated PDF with the task title as the file name
    doc.save(`${task.title}.pdf`);
  };

  return (
    <div className="task-item">
      <Typography variant="h6">{task.title}</Typography>  {/* Display task title */}
      <Typography variant="body1">{task.description}</Typography>  {/* Display task description */}
      <Typography variant="body2">{t("dueDate")}: {task.dueDate}</Typography>  {/* Display due date */}
      <Typography variant="body2">{t("status")}: {task.status}</Typography>  {/* Display task status */}

      {/* Buttons for Edit, Delete, and Change Status */}
      <div className="edit-delete-buttons">
        <Button onClick={() => onTaskEdit(task.id)} variant="contained" color="primary">
          {t("edit")}
        </Button>
        <Button onClick={() => onDeleteTask(task.id)} variant="contained" color="secondary">
          {t("delete")}
        </Button>
        <Button
          onClick={() => onStatusChange(task.id, task.status === "Pending" ? "Completed" : "Pending")}
          variant="contained"
          color={task.status === "Pending" ? "primary" : "success"}
        >
          {task.status === "Pending" ? t("markCompleted") : t("markPending")}
        </Button>

        {/* Button to save task as PDF */}
        <Button onClick={generatePDF} variant="contained" color="default">
          {t("saveAsPDF")}
        </Button>
      </div>
    </div>
  );
};

export default TaskItem;
